<!doctype html><html <?php language_attributes(); ?>><head>    <?php global $etheme_responsive, $woocommerce;; ?>	<meta charset="<?php bloginfo( 'charset' ); ?>" />    <?php if($etheme_responsive): ?>        <meta name="viewport" content="width=device-width, initial-scale=1, maximum-scale=1"/>    <?php endif; ?>	<link rel="shortcut icon" href="<?php etheme_option('favicon',true) ?>" />	<title><?php		/*		 * Print the <title> tag based on what is being viewed.		 */		global $page, $paged;		wp_title( '|', true, 'right' );		// Add the blog name.		bloginfo( 'name' );		// Add the blog description for the home/front page.		$site_description = get_bloginfo( 'description', 'display' );		if ( $site_description && ( is_home() || is_front_page() ) )			echo " | $site_description";		// Add a page number if necessary:		if ( $paged >= 2 || $page >= 2 )			echo ' | ' . sprintf( __( 'Page %s', ETHEME_DOMAIN ), max( $paged, $page ) );		?></title>        <!--[if IE 9]><link rel="stylesheet" type="text/css" href="<?php echo get_template_directory_uri().'/css/'; ?>ie9.css"><![endif]-->		<?php			if ( is_singular() && get_option( 'thread_comments' ) )				wp_enqueue_script( 'comment-reply' );			wp_head();		?></head><body class="landingpage-body">    <div class="new-wrapper">	<?php if(etheme_get_option('mobile_loader')): ?>		<div class="mobile-loader hidden-desktop">			<div id="floatingCirclesG"><div class="f_circleG" id="frotateG_01"></div><div class="f_circleG" id="frotateG_02"></div><div class="f_circleG" id="frotateG_03"></div><div class="f_circleG" id="frotateG_04"></div><div class="f_circleG" id="frotateG_05"></div><div class="f_circleG" id="frotateG_06"></div><div class="f_circleG" id="frotateG_07"></div><div class="f_circleG" id="frotateG_08"></div></div>                        <h5><?php _e('Loading the content...', ETHEME_DOMAIN); ?></h5>		</div>	<?php endif; ?>        <header class="site-header">            <div class="center-wrap clearfix">                <div class="logo">                    <a href="<?php echo site_url(); ?>" alt="<?php print get_bloginfo('name') .' - '. get_bloginfo('description'); ?>">                        <img src="<?php echo get_template_directory_uri(); ?>/images/logo-new.png"/>                    </a>                </div>                <?php if (is_page('Landing Page 2')): ?>                <div class="header-right2 clearfix">                    <?php dynamic_sidebar('header-landingpage2'); ?>                </div>                <?php endif; ?>                <?php if (is_page('Landing Page 1')): ?>                <div class="header-right3 clearfix">                    <?php dynamic_sidebar('header-landingpage'); ?>                </div>               <?php endif; ?>              </div>         </header>        	